                        endif
                        if(voronoi_cell_connect(i)==mark_voronoi_no_nab) then ! found this cell already in list
            !               found=.true.
                           exit
                        endif
                     end do
                     if(ikeep.ne.0) then
                        voronoi_cell_connect(ikeep)=mark_voronoi_no_nab
                     endif
            
                     return
                     end subroutine put_in_connect_for_voronoi_cell
            ! 
