                         if( abs( afilt_resid_tran_nnx(idim,i,j,k))<7.e-10) afilt_resid_tran_nnx(idim,i,j,k)=0.0
                       end do
                          write(27,*) afilt_resid_tran_nnx(idim,:,j,k)
