                       if(ndim==1) rnorm=2.0/afilt_nxnx(idim,ifilt_mid,1,1,ibase)
                       if(ndim==2) rnorm=4.0/afilt_nxnx(idim,ifilt_mid,ifilt_mid,1,ibase)
                       if(ndim==3) rnorm=6.0/afilt_nxnx(idim,ifilt_mid,ifilt_mid,ifilt_mid,ibase)
                       do k=1,krang
