                      !
                      do  INOD=1,NDNOD! Was loop 12000
                         ILOC=INOD
                         !
                         N(GPOI,ILOC) = &
                              SPECFU(NDIFF,LXGP,INOD,NDNOD,IPOLY,NODPOS)
                         !
