                    SUBROUTINE SMLINN_FACTORIZE(A,X,B,NMX,N)
                    IMPLICIT NONE
                    INTEGER NMX,N
                    REAL A(NMX,NMX),X(NMX),B(NMX)
                    REAL R
                    INTEGER K,I,J
            !     Form X = A^{-1} B
            !     Useful subroutine for inverse
            !     This sub overwrites the matrix A. 
                    DO K=1,N-1
                       DO I=K+1,N
                          A(I,K)=A(I,K)/A(K,K)
                       END DO
                       DO J=K+1,N
                          DO I=K+1,N
                             A(I,J)=A(I,J) - A(I,K)*A(K,J)
                          END DO
                       END DO
                    END DO
            !     
                  if(.false.) then
            !     Solve L_1 x=b
                    DO I=1,N
                       R=0.
                       DO J=1,I-1
                          R=R+A(I,J)*X(J)
                       END DO
                       X(I)=B(I)-R
                    END DO
            !     
            !     Solve U x=y
                    DO I=N,1,-1
                       R=0.
                       DO J=I+1,N
                          R=R+A(I,J)*X(J)
                       END DO
                       X(I)=(X(I)-R)/A(I,I)
                    END DO
                  endif
                    RETURN
                    END SUBROUTINE SMLINN_FACTORIZE
