                                 call swap_subdomains(ncola, cola,fina, whichd, nonods, starting_node, nsub)   
                              endif
                    !  
                    !          print *,'here6'
                              call space_filling_curve_from_ddm(ncola, cola,fina, whichd, nonods, &
                                              space_fill_curve_numbering, starting_node)
