                    ! pYTHON: 
                    ! COLM,FINDRM,NCOLM = SIMPLE_POSINMC_legacy(NDGLNO, TOTELE,NONODS,NLOC, MAX_NCOLM)
                          SUBROUTINE SIMPLE_POSINMC_legacy(COLM,FINDRM,NCOLM,  NDGLNO, TOTELE,NONODS,NLOC, MAX_NCOLM)
