                          INTEGER, INTENT(IN) :: FINA(NONODS+1),COLA(NCOLA),WHICHD(NONODS) 
                          REAL, INTENT(IN) :: X_ALL(NDIM,NONODS)
                    ! LOCAL VARIABLES...
                          INTEGER LNNOD,LNOD,I,COUNT,COUNT2,MICOL
                    
                                lnnod = 0
