                                      lnnod = lnnod + 1
                                      Map_L2S(i) = lnnod
                    !                  Lwnod(lnnod) = Wnod(i)
                                      LX_ALL(:,lnnod) = X_ALL(:,i)
                                   ENDIF
                                ENDDO
                                NONODS_S = LNNOD
                    !
                                count = 0
                                lnod = 0
                                DO i = 1 , NONODS
                                   IF ( Whichd(i).EQ.isub ) THEN
                                      lnod = lnod + 1
                                      Lfina(lnod) = count + 1
                                      DO count2 = Fina(i) , Fina(i+1) - 1
