                                            count = count + 1
                                            Lcola(count) = micol
                    !                        IF ( Havmat.EQ.1 ) La(count) = A(count2)
                                         ENDIF
                                      ENDDO
                                   ENDIF
                                ENDDO
                                Lfina(lnnod+1) = count + 1
                                lncola = count
                          END SUBROUTINE NEW_PTERS_FOR_SUB
