                                lncola = count
                                if(lncola>Maxnla) then
                                   stop 2821
                                endif
                    !
                                !WRITE (*,*) '*************LNNOD=' , lnnod
                    !            maxtot = max(4*lnnod,1000)
                    !            maxtot = max(4*lnnod,1000)
                                maxtot = Mmxtot
