                                      ENDIF
                                   ENDDO
                                ENDDO
                    !
                             ENDDO ! DO lcount = fitree(level) , fitree(level+1) - 1
                    !         !print *,'here3'
                          ENDDO ! DO level = 2 , nlevel - 1
                    !         !print *,'here4 exiting RECBIS'
                          deallocate( tree, lsubal )
