                    ! find all the nodes(put in Q) that QTEMP is
                    ! connected to that are not numbered.
                    ! and not already in Q.
                             DO i = 1 , nq
                                Qtemp(i) = Q(i)
                             ENDDO
                             nqtemp = nq
                             nq = 0
                             DO i = 1 , nqtemp
                                row = Qtemp(i)
                                DO count = Fina(row) , Fina(row+1) - 1
                                   icol = Cola(count)
                    ! see if already in Q.
                                   IF ( Blank(icol).NE.0 ) THEN
                                      IF ( Renum2(icol).EQ.0 ) THEN
                                         found = .FALSE.
                                         DO ii = 1 , nq
                                            IF ( icol.EQ.Q(ii) ) found = .TRUE.
                                         ENDDO
                                         IF ( .NOT.found ) THEN
                                            nq = nq + 1
                                            Q(nq) = icol
                                         ENDIF
                                      ENDIF
                                   ENDIF
                                ENDDO
                             ENDDO
