                                   i = Q(j)
                                   IF ( Blank(i).NE.0 ) THEN
                                      IF ( Renum2(i).EQ.0 ) THEN
                                         nval = 0
                                         DO count = Fina(i) , Fina(i+1) - 1
                                            icol = Cola(count)
                                            IF ( Blank(icol).NE.0 ) THEN
                                               IF ( Renum2(icol).EQ.0 ) nval = nval + 1
