                                         ENDDO
                                         IF ( nval.GT.mxval ) THEN
                                            mxval = nval
                                            mxnod = i
                                         ENDIF
                                      ENDIF
                                   ENDIF
                                ENDDO
                    !
                                iav = INT(REAL(nq)/2.+0.6)
                    ! Put to -1 for loops 910,920.
                                Renum2(mxnod) = -1
                                iii = 0
                                DO i = 1 , nq
                                   IF ( iii.LT.iav-1 ) THEN
                                      IF ( Renum2(Q(i)).EQ.0 ) THEN
                                         iii = iii + 1
                                         nod = nod + 1
                                         Renum2(Q(i)) = -nod
                                      ENDIF
                                   ENDIF
                                ENDDO
                                nod = nod + 1
                                Renum2(mxnod) = -nod
