                    ! connected to the l.h.s
                          INTEGER Hypdim , Proacr , Prodow , Nprocs
                          INTEGER Fulmat(Nprocs*Nprocs) , Imem(Nimem)
                    ! NIMEM=MAX(NPROCS*HYPDIM,4*NPROCS)*2 +(NPROCS+1)*2
